import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContract } from '../../../actions/Contract/contractAction';
import { fetchBuyerById } from '../../../actions/Buyer/buyerAction';
import '../Cards.css';
import { Card, CardHeader, CardMedia, CardContent, Typography } from '@mui/material';
import Numeration from '../../Numeration/Numeration';


const ContractCards = () => {
    const dispatch = useDispatch();
    const [buyerNames, setBuyerNames] = useState({});
    const { contract, currentPage, totalPages, limit, loading, error } = useSelector(state => state.contract);

    useEffect(() => {
        const fetchBuyers = async () => {
            const buyerIds = [...new Set(contract.map(item => item.buyer_id))];
            const promises = buyerIds.map(id => dispatch(fetchBuyerById(id)));
            const results = await Promise.all(promises);
            const newBuyerNames = {};
            results.forEach(result => {
                if (result.payload) {
                    newBuyerNames[result.payload.id] = result.payload.buyer_name;
                }
            });
            setBuyerNames(newBuyerNames);
        };

        if (contract.length > 0) { // Запускаем только если есть контракты
            fetchBuyers();
        }
    }, [dispatch, contract]);


    useEffect(() => {
        dispatch(fetchContract(currentPage, limit));
    }, [dispatch, currentPage, limit]);

    const handlePageChange = (page) => {
        dispatch(fetchContract(page, limit));
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>Ошибка: {error}</p>;

    return (
        <div className="wrapper">
            <div className="cards">
                {contract.map(item => (
                    <Card key={item.contract_number} sx={{ width: 345 }}>
                        <CardHeader
                            title={item.contract_number}
                        />
                        <CardContent>
                            <Typography variant="body2" sx={{ color: 'text.primary' }}>
                                Contract date: {item.contract_date}
                            </Typography>
                            <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                                Execution date: {item.execution_date}
                            </Typography >
                            <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                                Buyer name: {buyerNames[item.buyer_id] || 'Загрузка...'}
                            </Typography >
                        </CardContent>
                    </Card>
                ))}
            </div>
            <Numeration totalPages={totalPages} currentPage={currentPage} handlePageChange={handlePageChange} />
        </div>
    );
};

export default ContractCards;
