import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchBuyer, deleteBuyer } from '../../../actions/Buyer/buyerAction';
import '../Cards.css';
import { Card, CardHeader, CardMedia, CardContent, Typography, CardActions, Button, Dialog, DialogTitle, DialogContent, DialogActions, DialogContentText  } from '@mui/material';
import Numeration from '../../Numeration/Numeration';



const BuyerCards = () => {
  const dispatch = useDispatch();
  const { buyer, currentPage, totalPages, limit, loading, error } = useSelector(state => state.buyer);

  useEffect(() => {
    dispatch(fetchBuyer(currentPage, limit));
    console.log(buyer);
  }, [dispatch, currentPage, limit]);

  const handlePageChange = (page) => {
    dispatch(fetchBuyer(page, limit));
  };

  const navigate = useNavigate()

  const goToAddBuyer = () => {
    navigate("/addBuyer");
  }

  const handleEdit = (item) => {
    navigate(`/editBuyer/${item.buyer_id}`, { state: item }); 
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>Ошибка: {error}</p>;

  return (
    <div className="wrapper">
      <div className="cards">
        {buyer.map(item => (
          <Card key={item.buyer_id} sx={{ width: 345 }}>
            <CardHeader
              title={item.buyer_name}
            />
            <CardContent>
              <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                {item.buyer_address}
              </Typography>
              <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                {item.buyer_phone}
              </Typography >
            </CardContent>
            <CardActions>
              <Button size="small" color='primary.contrastText' onClick={() => handleEdit(item)}>Редактировать</Button>
              <Button size="small" color='primary.contrastText'>Удалить</Button>
            </CardActions>
          </Card>
        ))}
      </div>
      <Numeration totalPages={totalPages} currentPage={currentPage} handlePageChange={handlePageChange} />
      <Button onClick={goToAddBuyer} color='primary.contrastText' sx = {{marginBottom: "50px"}}>Добавить покупателя</Button>
    </div>
  );
};

export default BuyerCards;
