import axios from 'axios';
import { FETCH_FURNITURE } from '../actionTypes';

export const fetchFurniture = (page = 1, limit = 10) => async (dispatch) => {
  try {
    const response = await axios.get(`http://localhost:8000/api/furnitureModel/?page=${page}&limit=${limit}`);
    dispatch({ type: FETCH_FURNITURE_SUCCESS, payload: { ...response.data, currentPage: page, limit } });
  } catch (error) {
    console.error('Ошибка при получении данных:', error);
    //dispatch({ type: FETCH_FURNITURE_ERROR, payload: error.message }); 
  }
};

