import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchFurniture } from '../../actions/furnitureModelActions';
import './FurnitureModelCards.css'
import { styled } from '@mui/material/styles';
import { red } from '@mui/material/colors';
import { Card, CardHeader, CardMedia, CardContent, CardActions, Collapse, Avatar, Typography } from '@mui/material';
// import FavoriteIcon from '@mui/icons-material/Favorite';
// import ShareIcon from '@mui/icons-material/Share';
// import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
// import MoreVertIcon from '@mui/icons-material/MoreVert';

const FurnitureModels = () => {
  const dispatch = useDispatch();
  const { furniture, currentPage, totalPages, limit, loading, error } = useSelector(state => state.furnitureModels);

  useEffect(() => {
    dispatch(fetchFurniture(currentPage, limit));
  }, [dispatch, currentPage, limit]);

  const handlePageChange = (page) => {
    dispatch(fetchFurniture(page, limit));
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>Ошибка: {error}</p>;

  return (
    <div className="wrapper">
      <div className="cards">
        {furniture.map(item => (
          <Card key = {item.furniture_model_id} sx={{ maxWidth: 345 }}>
            <CardHeader
              title={item.furniture_name}
              subheader={item.model}
            />
            {/* <CardMedia
              component="img"
              height="194"
              image="/static/images/cards/paella.jpg"
              alt="text"
            /> */}
            <CardContent>
              <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                {item.characteristics}
              </Typography>
              <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                {item.price}
              </Typography >
            </CardContent>
          </Card>
          // <div key={item.furniture_model_id}>
          //   <p>{item.furniture_name}</p>
          // </div>
        ))}
        <div>
          <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
            &lt;
          </button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
            &gt;
          </button>
        </div>
      </div>
    </div>
  );
};

export default FurnitureModels;
