import axios from 'axios';
import { FETCH_FURNITURE, FETCH_FURNITURE_SUCCESS, FETCH_FURNITURE_FAILURE } from '../actionTypes';

export const fetchFurniture = (page = 1, limit = 10) => async (dispatch) => {
  dispatch({ type: FETCH_FURNITURE }); // Устанавливаем loading = true
  try {
    const response = await axios.get(`http://localhost:8000/api/furnitureModel/?page=${page}&limit=${limit}`);
    dispatch({ type: FETCH_FURNITURE_SUCCESS, payload: { ...response.data, currentPage: page, limit } });
  } catch (error) {
    dispatch({ type: FETCH_FURNITURE_FAILURE, payload: error.message });
  }
};
