import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchFurniture } from '../../actions/furnitureModelActions';
import './FurnitureModelCards.css'

const FurnitureModels = () => {
  const dispatch = useDispatch();
  const { furniture, currentPage, totalPages, limit, loading, error } = useSelector(state => state.furnitureModels);

  useEffect(() => {
    dispatch(fetchFurniture(currentPage, limit));
  }, [dispatch, currentPage, limit]);

  const handlePageChange = (page) => {
    dispatch(fetchFurniture(page, limit));
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>Ошибка: {error}</p>;

  return (
    <div className="wrapper">
      <div className="cards">
        {furniture.map(item => (
          <div key={item.furniture_model_id}>
            <p>{item.furniture_name}</p>
          </div>
        ))}
        <div>
          <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
            &lt;
          </button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
            &gt;
          </button>
        </div>
      </div>
    </div>
  );
};

export default FurnitureModels;
