import { FETCH_BUYER, FETCH_BUYER_SUCCESS, FETCH_BUYER_FAILURE } from '../actionTypes';

const initialState = {
  buyer: [],
  total: 0,
  currentPage: 1,
  totalPages: 0,
  limit: 10,
  loading: true, 
  error: null,
};

const buyerSlice = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_BUYER:
      return { ...state, loading: true, error: null };
    case FETCH_BUYER_SUCCESS:
      return {
        ...state,
        buyer: action.payload.data,
        total: action.payload.total,
        currentPage: action.payload.currentPage,
        totalPages: action.payload.pages,
        loading: false,
        error: null,
      };
    case FETCH_BUYER_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default buyerSlice;
