import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContract } from '../../../actions/Contract/contractAction';
import { fetchBuyerById } from '../../../actions/Buyer/buyerAction';
import '../Cards.css';
import { Card, CardHeader, CardMedia, CardContent, Typography } from '@mui/material';
import Numeration from '../../Numeration/Numeration';


const ContractCards = () => {
    const dispatch = useDispatch();
    const [buyerNames, setBuyerNames] = useState({});
    const { contract, currentPage, totalPages, limit, loading, error } = useSelector(state => state.contract);

    const fetchBuyerName = (buyerId) => {
        dispatch(fetchBuyerById(buyerId));
    };

    useEffect(() => {
        const fetchBuyersAndContracts = async () => {
            await dispatch(fetchContract(currentPage, limit)); // Сначала загружаем контракты

            // Затем асинхронно загружаем имена покупателей
            const buyerIds = [...new Set(contract.map(item => item.buyer_id))]; // Уникальные ID покупателей
            const promises = buyerIds.map(id => dispatch(fetchBuyerById(id)));

            Promise.all(promises).then((results) => {
                const newBuyerNames = {};
                results.forEach(result => {
                    if (result.payload) { // Проверка на наличие payload, на случай ошибки
                        newBuyerNames[result.payload.id] = result.payload.buyer_name; // Замените buyer_name на нужное поле
                    }
                });
                setBuyerNames(newBuyerNames);
            });
        };

        fetchBuyersAndContracts();
    }, [dispatch, currentPage, limit, contract]); // contract добавлен в зависимости

    const handlePageChange = (page) => {
        dispatch(fetchContract(page, limit));
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>Ошибка: {error}</p>;

    return (
        <div className="wrapper">
            <div className="cards">
                {contract.map(item => (
                    <Card key={item.contract_number} sx={{ width: 345 }}>
                        <CardHeader
                            title={item.contract_number}
                        />
                        <CardContent>
                            <Typography variant="body2" sx={{ color: 'text.primary' }}>
                                Contract date: {item.contract_date}
                            </Typography>
                            <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                                Execution date: {item.execution_date}
                            </Typography >
                            <Typography variant="body2" sx={{ color: 'text.primary', marginTop: "20px" }}>
                                Buyer name: {fetchBuyerName(item.buyer_id)}
                            </Typography >
                        </CardContent>
                    </Card>
                ))}
            </div>
            <Numeration totalPages={totalPages} currentPage={currentPage} handlePageChange={handlePageChange} />
        </div>
    );
};

export default ContractCards;
