import { FETCH_FURNITURE } from '../actionTypes';

const initialState = {
  furniture: [],
  total: 0,
  currentPage: 1,
  totalPages: 0,
  limit: 10,
  loading: true,
  error: null,
};

const furnitureSlice = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_FURNITURE:
      return {
        ...state,
        furniture: action.payload.data,
        total: action.payload.total,
        currentPage: parseInt(action.payload.currentPage) || 1, //Обработка отсутствующего currentPage
        totalPages: action.payload.pages,
        loading: false,
        error: null,
      };
    // case FETCH_FURNITURE_ERROR:
    //   return {
    //     ...state,
    //     loading: false,
    //     error: action.payload,
    //   };
    default:
      return state;
  }
};

export default furnitureSlice;
