import { FETCH_FURNITURE, FETCH_FURNITURE_SUCCESS, FETCH_FURNITURE_FAILURE } from '../actionTypes';

const initialState = {
  furniture: [],
  total: 0,
  currentPage: 1,
  totalPages: 0,
  limit: 10,
  loading: true, // Добавлено начальное состояние loading
  error: null,
};

const furnitureSlice = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_FURNITURE:
      return { ...state, loading: true, error: null }; // Устанавливаем loading = true при начале запроса
    case FETCH_FURNITURE_SUCCESS:
      return {
        ...state,
        furniture: action.payload.data,
        total: action.payload.total,
        currentPage: action.payload.currentPage,
        totalPages: action.payload.pages,
        loading: false,
        error: null,
      };
    case FETCH_FURNITURE_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default furnitureSlice;
